import React, { Component } from 'react';
import axios from 'axios';
import './Login';

import {
Link,
Redirect
} from 'react-router-dom';

class Login extends Component {

    state = {
        username: '',
        email: '',
        password: '',
        success: '',
        error: '',
        redirect: false
    };

    handleChange = (event) => {
        const key = event.target.name;
        const value = event.target.value;

        this.setState({
            [key]: value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();

        axios.post('http://backenddrupal.test/user/login?_format=json', {
            name: this.state.name,
            pass: this.state.password
        })
        .then(function (response) {
            this.setState({
            'success': 'Login successful',
            'error': ''
            });

            localStorage.setItem('username', response.data.current_user.name);
            localStorage.setItem('uid', response.data.current_user.uid);
            localStorage.setItem('csrf_token', response.data.csrf_token);
            localStorage.setItem('logout_token', response.data.logout_token);
            localStorage.setItem('auth', window.btoa(this.state.name + ':' + this.state.password));

            this.setState({redirect: true});
        });

    }

    render(){

        if (this.state.redirect) {
            return (
                <Redirect to="/" />
            );
        }

    return (
        <div >
        <div>
            <form onSubmit={this.handleSubmit}>
                <div><input name="name" value={this.state.name} onChange={this.handleChange} required type="text" placeholder="Enter username" /></div>
                <div><input name="password" value={this.state.password} onChange={this.handleChange} required type="password" placeholder="Enter password" /></div>
            <button type="submit" >Login</button>
            <div>
                <p className="success">{this.state.success}</p>
                <p className="error" dangerouslySetInnerHTML={{__html: this.state.error}} />
            </div>
            <Link to="/user/register">Don't have an account?</Link>
            </form>
        </div>
        </div>
        );
    }
}

export default Login
