import React, { Component } from 'react';
import './Contact.css';


class Contact extends Component{

    state = {
        name: undefined,
        email: undefined,
        subject: undefined,
        message: undefined
    };

    handleChange(event) {
        const key = event.target.name;
        const value = event.target.value;

        this.setState({
            [key]: value
        })
    }

    handleSubmit(event) {
        event.preventDefault();

        var self = this;

        axios.post('http://marvel.test/contact_message?_format=json', {
            contact_form: [{"target_id":"feedback"}],
            name: [{"value": this.state.name}],
            mail: [{"value": this.state.email}],
            subject: [{"value": this.state.subject}],
            message: [{"value": this.state.message}]
            })
            .then(function (response) {
                self.setState({
                'success': 'Message sent',
                'error': ''
                });
            })
            .catch(function (error) {
                var errorResponse = error.response.data.message;
                errorResponse = errorResponse.replace(/(?:\r\n|\r|\n)/g, '<br />');

                self.setState({
                'success': '',
                'error': errorResponse
                });
            });
    }


    render(){

        return(
            <div>
                <form>
                    <div><input type ='text' placeholder='Enter name'/></div>
                    <div><input type ='text' placeholder='Enter email'/></div>
                    <div><input type ='text' placeholder='Enter subject'/></div>
                    <div><textarea type ='text' placeholder='Enter message'/></div>
                    <div><input type ='button' value='Send Message'/></div>
                </form>
            </div>
        );
    }
};

export default Contact;
