import React from 'react';


 const RightArrow = ({ nextSlide, coolButtons }) => {
  return (
    <div className={coolButtons ? 'right-arrow cool-buttons' : 'right-arrow'} onClick={nextSlide}>
      <img src="img/slider-right-arrow.svg" />
    </div>
  )
};

const LeftArrow = ({ prevSlide, coolButtons }) => {
    return (
      <div className={coolButtons ? 'left-arrow cool-buttons' : 'left-arrow'} onClick={prevSlide}>
        <img src="img/slider-left-arrow.svg" />
      </div>
    )
  };


  export default {
      RightArrow,
      LeftArrow
  }
