    }

    render(){

      return (
        <div>
          <table>
            <thead>
              <tr>
                <td>NID</td>
                <td>Title</td>
              </tr>
            </thead>
            <tbody>
                {this.state.nodes.map((node, index) => {
                  // iterate over the nodes array and map them to "li" elements
                  return (
                    <tr key={index}>
                      <td>{node.nid}</td>
                      <td><a href={node.path} >{node.title}</a></td>
                    </tr>
                  )
                  })}
            </tbody>
          </table>
        </div>
      );
    };
};

export default Article;
