import React, { Component } from 'react';
import {Redirect, Link} from 'react-router-dom';
import axios from 'axios';
import './Register.css';

class Register extends Component{

    state = {
        username: undefined,
        email: undefined,
        password: undefined,
        confirmPassword: undefined,
        success: undefined,
        error: undefined,
        redirect: false
    };

    handleChange = (event) =>{
        const key = event.target.name;
        const value = event.target.value;

        this.setState({
            [key]: value
        })
    }

    handleSubmit = (event) =>{
        event.preventDefault();

        if (this.state.password !== this.state.confirmPassword) {
            this.setState({
            'success': '',
            'error': 'Passwords do not match'
            });
            return;
        }

        axios.post('http://backenddrupal/user/register?_format=json', {
            name: [{"value": this.state.username}],
            mail: [{"value": this.state.email}],
            pass: [{"value": this.state.password}]
        })
        .then(function (response) {
            this.setState({
            'success': 'Registration successful',
            'error': ''
            });
            this.setState({redirect: true});
        })
    }

    render(){

        const {username, email, password, confirmPassword, redirect} = this.state;

        if (redirect) {
            return (
                <Redirect to="/login" />
            );
        }

        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div><input type ='text' name = 'username' value={username} onChange={this.handleChange} placeholder='Enter username'/></div>
                    <div><input type ='text' name = 'email' value={email} onChange={this.handleChange} placeholder='Enter email'/></div>
                    <div><input type ='text' name = 'password' value={password} onChange={this.handleChange} placeholder='Enter password'/></div>
                    <div><input type ='text' name = 'confirmPassword' value={confirmPassword} onChange={this.handleChange} placeholder='Confirm password'/></div>
                    <div><input type ='button' value='Register'/></div>
                </form>
                <div>
                    <Link to="/login">Already have an account?</Link>
                </div>
            </div>
        );
    }
};

export default Register;
