import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios'
import './Article.css';

class Article extends Component{

    state = {
        articles: [],
        article_title: '',
        article_body: ''
    };

    fetchArticle = (nid) =>{
        let id;
        if (nid !== undefined) {
          id = nid;
        }
        else if (this.props.match.params.id !== undefined) {
          id = this.props.match.params.id;
        }
        else {
          id = 6;
        }

        this.serverRequest = axios.get('http://backenddrupal.test/' + id + '?_format=json')
        .then(function(result){
          let body = result.data.body["0"].value;
          this.setState({
            article_title: result.data.title["0"].value,
            article_body: body.replace('/sites/default/files', 'http://nohead.test/sites/default/files')
          });
        })
      }

      fetchArticleTitles = () =>{

        this.serverRequest = axios.get('http://backenddrupal.test/api/v1/articles/list')
        .then(function(result){
          this.setState({
            articles: result.data
          });
        })
      }

      componentDidMount () {
        this.fetchArticleTitles();
        this.fetchArticle();
      }

    render(){

      let rows = [];
      this.state.articles.forEach(function(article, index) {
        if (article.title.toLowerCase().indexOf(this.state.keyword.toLowerCase()) !== -1) {
          var path = '/articles/' + article.nid;
          rows.push(<NavLink key={article.nid} data-value={article.nid} onClick={this.updateSelectedArticle} to={path} className="list-group-item list-group-item-action">{article.title}</NavLink>);
        }
      });

      return (
        <div className="row top-buffer">
          <div className="col-md-4">
            <form>
              <div className="form-group">
                <input name="keyword" value={this.state.keyword} onChange={this.updateSearchKeyword} type="text" className="form-control" placeholder="Search articles" />
              </div>
            </form>
            <div className="list-group">
              {rows}
            </div><br />
          </div>
          <div className="col-md-8">
            <div className="card text-center">
              <div className="card-header">
                {this.state.article_title}
              </div>
              <div className="card-block" dangerouslySetInnerHTML={{__html: this.state.article_body}} />
              <div className="card-footer text-muted text-left">
                <em><small>Umsebenzi ka Adonis</small></em>
              </div>
            </div>
          </div>
        </div>
      );
    };
};

export default Article;
