import React, { Component } from 'react';
import axios from 'axios'
import './Article.css';

class Article extends Component{

    state = {
        articles: [],
        article_title: '',
        article_body: ''
    };

    fetchArticle = (nid) =>{
        let id;
        if (nid !== undefined) {
          id = nid;
        }
        else if (this.props.match.params.id !== undefined) {
          id = this.props.match.params.id;
        }
        else {
          id = 6;
        }

        this.serverRequest = axios.get('http://marvel.test/node/' + id + '?_format=json')
        .then(function(result){
          let body = result.data.body["0"].value;
          this.setState({
            article_title: result.data.title["0"].value,
            article_body: body.replace('/sites/default/files', 'http://nohead.test/sites/default/files')
          });
        })
      }

      fetchArticleTitles() {
        var self = this;
        this.serverRequest = axios.get('http://marvel.test/api/v1/articles/list')
        .then(function(result){
          self.setState({
            articles: result.data
          });
        })
      }

      componentDidMount () {
        this.fetchArticleTitles();
        this.fetchArticle();
      }

    render(){
        return(
            <div>
                This is the Article Page
            </div>
        );
    };
};

export default Article;
